def translate_rna_to_protein(rna_string):
    """
    Translates an RNA string into a protein string, stopping at a stop codon.
    """
    
    # The standard RNA codon table, mapping 3-base codons to 1-letter amino acids.
    # 'Stop' is used as a placeholder for the three stop codons.
    codon_table = {
        'UUU': 'F', 'UUC': 'F', 'UUA': 'L', 'UUG': 'L',
        'UCU': 'S', 'UCC': 'S', 'UCA': 'S', 'UCG': 'S',
        'UAU': 'Y', 'UAC': 'Y', 'UAA': 'Stop', 'UAG': 'Stop',
        'UGU': 'C', 'UGC': 'C', 'UGA': 'Stop', 'UGG': 'W',
        'CUU': 'L', 'CUC': 'L', 'CUA': 'L', 'CUG': 'L',
        'CCU': 'P', 'CCC': 'P', 'CCA': 'P', 'CCG': 'P',
        'CAU': 'H', 'CAC': 'H', 'CAA': 'Q', 'CAG': 'Q',
        'CGU': 'R', 'CGC': 'R', 'CGA': 'R', 'CGG': 'R',
        'AUU': 'I', 'AUC': 'I', 'AUA': 'I', 'AUG': 'M',
        'ACU': 'T', 'ACC': 'T', 'ACA': 'T', 'ACG': 'T',
        'AAU': 'N', 'AAC': 'N', 'AAA': 'K', 'AAG': 'K',
        'AGU': 'S', 'AGC': 'S', 'AGA': 'R', 'AGG': 'R',
        'GUU': 'V', 'GUC': 'V', 'GUA': 'V', 'GUG': 'V',
        'GCU': 'A', 'GCC': 'A', 'GCA': 'A', 'GCG': 'A',
        'GAU': 'D', 'GAC': 'D', 'GAA': 'E', 'GAG': 'E',
        'GGU': 'G', 'GGC': 'G', 'GGA': 'G', 'GGG': 'G',
    }
    
    protein_string = ""
    
    # Iterate through the RNA string in steps of 3 (the length of a codon)
    for i in range(0, len(rna_string), 3):
        # Get the current codon
        codon = rna_string[i:i+3]
        
        # Ensure we have a full codon
        if len(codon) < 3:
            break
            
        # Look up the amino acid in the table
        amino_acid = codon_table.get(codon)
        
        # If the codon is a "Stop" codon, end the translation
        if amino_acid == 'Stop':
            break
        
        # Otherwise, append the amino acid to the protein string
        if amino_acid:
            protein_string += amino_acid
            
    return protein_string

# --- Example Usage ---

# This is the RNA string from your screenshot's "Sample Dataset"
